mapper.insert:
INSERT INTO resume_form (
    RCRT_NO,
    RPROC_ORDER,
    SCR_EDU,
    SCR_LANG,
    SCR_CAREER,
    SCR_CER,
    SCR_IA,
    SCR_OS,
    SCR_ETC
) VALUES (
    #{rcrtNo}
    , #{rprocOrder}
    , #{scrEdu}
    , #{scrLang}
    , #{scrCareer}
    , #{scrCer}
    , #{scrIa}
    , #{scrOs}
    , #{scrEtc}
)

		INSERT INTO resume_score (
		    rcrt_no,
		    rproc_order,
		    apl_no,
		    scr_edu,
		    scr_lang,
		    scr_career,
		    scr_cer,
		    scr_ia,
		    scr_os,
		    scr_etc
		) VALUES (
			#{rcrtNo, jdbcType=VARCHAR}
			, #{rprocOrder, jdbcType=NUMERIC}
			, #{aplNo, jdbcType=VARCHAR}
    		, #{scrEdu, jdbcType=NUMERIC}
		    , #{scrLang, jdbcType=NUMERIC}
		    , #{scrCareer, jdbcType=NUMERIC}
		    , #{scrCer, jdbcType=NUMERIC}
		    , #{scrIa, jdbcType=NUMERIC}
		    , #{scrOs, jdbcType=NUMERIC}
		    , #{scrEtc, jdbcType=NUMERIC}
		 )

mapper.update:
UPDATE resume_score
SET
    scr_edu = #{scrEdu, jdbcType=NUMERIC}
    , scr_lang = #{scrLang, jdbcType=NUMERIC}
    , scr_career = #{scrCareer, jdbcType=NUMERIC}
    , scr_cer = #{scrCer, jdbcType=NUMERIC}
    , scr_ia = #{scrIa, jdbcType=NUMERIC}
    , scr_os = #{scrOs, jdbcType=NUMERIC}
    , scr_etc = #{scrEtc, jdbcType=NUMERIC}
WHERE
  rcrt_no = #{rcrtNo}
	AND rproc_order	= #{rprocOrder}
	AND apl_no = #{aplNo}
